document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelectorAll("[data-modal]"),t=document.body,o=document.querySelectorAll(".modal__close"),c=document.querySelectorAll(".modal");function l(e){e.querySelector(".modal__content").removeAttribute("style"),setTimeout(()=>{e.classList.remove("show"),t.classList.remove("no-scroll")},300)}e.forEach(e=>{e.addEventListener("click",e=>{let o=e.currentTarget.getAttribute("data-modal"),c=document.getElementById(o),l=c.querySelector(".modal__content");l.addEventListener("click",e=>{e.stopPropagation()}),c.classList.add("show"),t.classList.add("no-scroll"),setTimeout(()=>{l.style.transform="none",l.style.opacity="1"},300)})}),o.forEach(e=>{e.addEventListener("click",e=>{l(e.currentTarget.closest(".modal"))})}),c.forEach(e=>{e.addEventListener("click",e=>{l(e.currentTarget)})})});