document.addEventListener("DOMContentLoaded",()=>{document.querySelector(".burger__button").onclick=function(){document.querySelector(".burger__button-icon").classList.toggle("active"),document.querySelector(".burger__menu").classList.toggle("active")};const e=document.querySelectorAll(".dropdown");for(item of e)item.addEventListener("click",(function(){if(this.classList.contains("active"))this.classList.remove("active");else{for(el of e)el.classList.remove("active");this.classList.add("active")}}))});